import React from 'react';
import './scss/Blog.scss'

import { Container, Row, Col } from 'react-grid-system';
import { setConfiguration } from 'react-grid-system';
import {postArray} from './Array.jsx'
setConfiguration({ maxScreenClass: 'xl' });


export const Blog = () => {
    
        function createMarkup() {
            return {
                __html: `
                <div className="blog-item">
                <img src="${postArray[i].imgUrl}" />
                <span>${postArray[i].author}</span>
                <span>${postArray[i].dataCreated}</span> 
                <h1>${postArray[i].articleTitle}</h1>
                </div>
               `};
          }
          
    function MyComponent() {
        for (let i = 0; i < postArray.length; i++)
            return <div dangerouslySetInnerHTML={createMarkup()} />;
          }
    

    return (
        <div className="grid-container__wrap">
    
        <div className="blog-item">{MyComponent()}</div>       
    
        </div>
    )
}
