import React from 'react';
import { postArray } from './Array.jsx'
import './scss/Article.scss'
import crown from './img/icons/crown.svg'

function createMarkup(i) {
    return {
        __html: `                  
        <div class="article-head">
                    <a href="/login" class="article__author">${postArray[i].author} <img class="crown-icn" src="${crown}" data-tooltip="Admin"></a>
                    <span class="article__datacreate"> &sdot; ${postArray[i].dataCreated} &sdot; 1 min</span>
        </div>
        <h2 class="article__title">${postArray[i].articleTitle}</h2>
        <p class="article__descript">${postArray[i].shortTxt}</p>
        <img class="article__img" src="${postArray[i].imgUrl}" />
        </div>
       `};
  }
  
function MyComponent(i) {

        return (<div className="article-content-wrap" dangerouslySetInnerHTML={createMarkup(i)} />)      
                ;
  }


export const Article0 = () => {
    return (
        MyComponent(0)
    )
}

export const Article1 = () => {
    return (
        <h2 className="article-title">1={postArray[1].articleTitle}</h2>
    )
}

export const Article2 = () => {
    return (
        <h2 className="article-title">2={postArray[2].articleTitle}</h2>
    )
}

export const Article3 = () => {
    return (
        <h2 className="article-title">3={postArray[3].articleTitle}</h2>
    )
}

export const Article4 = () => {
    return (
        <h2 className="article-title">4={postArray[4].articleTitle}</h2>
    )
}

export const Article5 = () => {
    return (
        <h2 className="article-title">5={postArray[5].articleTitle}</h2>
    )
}
