import React from 'react';
import './scss/Blog.scss'

import {postArray} from './Array.jsx'



export const Blog = () => {
    
        function createMarkup(elem) {
            return {
                __html: `
                <div class="post-item">           
                <img class="post-item__img" src="${elem.imgUrl}" />
                <span class="post-item__author">${elem.author}</span>
                <span class="post-item__datacreate">${elem.dataCreated}</span> 
                <span class="post-item__title">${elem.articleTitle}</span>
                <p class="post-item__descript">${elem.shortTxt}</p>
                </div> 
               `};
          }
          
    function MyComponent() {
        for (elem of postArray) {
            return <div className="post-item__content" dangerouslySetInnerHTML={createMarkup(elem)} />      
              }
            ;
          }
    

    return (
        MyComponent()
        /*<div className="post-container__wrap">
            <div className="post-item">{MyComponent(0)}</div> 
            <div className="post-item">{MyComponent(1)}</div>
            <div className="post-item">{MyComponent(2)}</div>
            <div className="post-item">{MyComponent(3)}</div>
            <div className="post-item">{MyComponent(4)}</div>      
            <div className="post-item">{MyComponent(5)}</div>       
        </div>*/
    )
}
