import React from 'react';
import './scss/Blog.scss'

import {postArray} from './Array.jsx'



export const Blog = () => {
    
        function createMarkup(i) {
            return {
                __html: `             
                <img src="${postArray[i].imgUrl}" />
                <span>${postArray[i].author}</span>
                <span>${postArray[i].dataCreated}</span> 
                <h1>${postArray[i].articleTitle}</h1>
               `};
          }
          
          function MyComponent(i) {
            return <div dangerouslySetInnerHTML={createMarkup(i)} />;
          }
    

    return (
    <div className="grid-container__wrap">
       <div className="blog-item"></div> {MyComponent(0)}
        {MyComponent(1)}
        {MyComponent(2)}
        {MyComponent(3)}
        {MyComponent(4)}       
        {MyComponent(5)}       
    </div>
    )
}
