import React from 'react';
import './scss/Blog.scss'

import { Container, Row, Col } from 'react-grid-system';
import { setConfiguration } from 'react-grid-system';
import {postArray} from './Array.jsx'
setConfiguration({ maxScreenClass: 'xl' });


export const Blog = () => {
    
    let arr = (i) => {
        
        const content = `${postArray[i].articleTitle}` 
        for (postArray[i] of postArray){
            postArray[i].innerHtml = content
        }
        console.log(postArray[i])
        return postArray[i].innerHtml = content
    }
    
        function createMarkup(i) {
            return {__html: `${postArray[i].articleTitle}`};
          }
          
          function MyComponent(i) {
            return <div dangerouslySetInnerHTML={createMarkup(i)} />;
          }
    

    return (
        <Container className="grid-container__wrap">
    <Row>
                <Col sm={4}>
                    <span>{MyComponent(3)}</span>
            One of three columns
    </Col>
        <Col sm={4}>
            One of three columns
    </Col>
        <Col sm={4}>
            One of three columns
    </Col>
    </Row>
    </Container>
    )
}
