import React, { Component } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import '../components/SlideShow.scss'


import { image1 } from '../assets/img/home/01.jpg';
import { image2 } from '../assets/img/home/02.jpg';
import { image3 } from '../assets/img/home/03.jpg';
import { image4 } from '../assets/img/home/04.jpg';
import { image5 } from '../assets/img/home/05.jpg';
import { image6 } from '../assets/img/home/06.jpg';
import { image7 } from '../assets/img/home/07.jpg';
import { image8 } from '../assets/img/home/08.jpg';
import { image9 } from '../assets/img/home/09.jpg';
import { image10 } from '../assets/img/home/10.jpg';
import { image11 } from '../assets/img/home/11.jpg';
import { image12 } from '../assets/img/home/12.jpg';

 



class Responsive extends Component {
  render() {
    let settings = {
      dots: false,
      infinite: false,
      speed: 500,
      slidesToShow: 5,
      slidesToScroll: 5,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
      };
     
      let imagesToScroll = [
       '../assets/img/home/01.jpg',
          '../assets/img/home/02.jpg',
            '../assets/img/home/03.jpg',
              '../assets/img/home/04.jpg',
              '../assets/img/home/05.jpg',
              '../assets/img/home/06.jpg',
              '../assets/img/home/07.jpg',
              '../assets/img/home/08.jpg',
              '../assets/img/home/09.jpg',
              '../assets/img/home/10.jpg',
              '../assets/img/home/11.jpg',
              '../assets/img/home/12.jpg'

      ]
    
    return (
      <div>
        <h2> Responsive </h2>
        <Slider {...settings}>
          <div>
            <div className='slide-item'
              style={{
                backgroundImage: `url('${imagesToScroll[0]}')`,
                backgroundSize: `cover`
              }}></div>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
          <div>
            <h3>7</h3>
          </div>
          <div>
            <h3>8</h3>
          </div>
        </Slider>
      </div>
    );
  }
}

export {Responsive}
